import { connect } from 'react-redux';
import { login, 
         input_username, 
         input_password, 
         input_rest }    from '../../actions';

import Login from '../../services';

import './login-page.css';


// "admin@ship.ru"   "secret2"

const LoginPage = ({login, 
                    input_username, 
                    input_password, 
                    input_rest }) => {

    return <div className='login-page'>

        <form>
          <input 
            className='login-form'
            onChange={ (event) => {
              event.preventDefault()
              input_username(event.target.value) } 
            }>

          </input>

          <input 
            className='password-form'
            onChange={ (event) => {
              event.preventDefault()
              input_password(event.target.value) } 
            }>

          </input>

          <button 
            className='submit-button'
            onClick={ (event) => {
                       event.preventDefault()

                       // **** temporary measure ****
                       // TODO: see docs to know: how to get this poles to different users 
                       input_rest({ 
                          client_id: '1',
                          client_secret: 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT',
                          grant_type: 'password'})

                       // sending already filled form to server
                       Login() } 
                    }>
              Войти
          </button>

        </form>

    </div>

}



