const updateLoginData = (state, action) => {
    console.log('action type is ', action.type, action.payload);

    if (state === undefined )
        return{
            username: '',
            password: '',
            client_id: '', 
            client_secret: '',
            grant_type: '' }


    console.log('\nredux-store: ', state)

    // for destructoring action.payload must be not undefined - that`s why we declaired action = { payload: {}}
    const {
            username,
            password,
            client_id, 
            client_secret,
            grant_type }   = action.payload


    switch (action.type) {

      case 'INPUT_USER':
        console.log('Username: ', username)
        return { ...state,  username }

      case 'INPUT_PSWD':
        return { ...state, password }

      case 'INPUT_REST':
        return { 
                  ...state, 
                  client_id, 
                  client_secret, 
                  grant_type    }  

      /*
      // TODO: 1) remove to ../services - bc LOGIN makes no change on storage - just "side effect"
      //       2) mb not to create FormData each api-call 
      case 'LOGIN':
        let formdata = new FormData()
        formdata.append("username", username)
        formdata.append("password", password)
        formdata.append("client_id", client_id)
        formdata.append("client_secret", client_secret)
        formdata.append("grant_type", grant_type)

        let requestOptions = {
          method: 'POST',
          body: formdata,
          redirect: 'follow'
        }

        return fetch("http://server.clerc.ru/api/v2.0/auth/login", requestOptions)
               .then(response => response.text())
               .then(result => console.log('on login response',result))
               .catch(error => console.log('error', error))

    */

      default:
        return state
    }

  };

export default updateLoginData
