import React from 'react';
import { connect } from 'react-redux';
import { 
         input_username, 
         input_password, 
         input_rest,
         update_token, 
         add_contracts,
         Login
         }    from '../../actions';

import {getApiKey, getContracts} from '../../services';

import './login-page.css';

import styled from 'styled-components'



const InputLogin = styled.TextInput`
  type: "text",
  ::placeholder{
     "wtf"
    },
  background: ${props => props.primary ? "palevioletred" : "white"};
  color: ${props => props.primary ? "white" : "palevioletred"};

  background: rgba(0, 0, 0, 0.1);
  border-radius: 10px;

  font-size: 2em;
  margin: 2em;
  padding: 0.25em 1em;

`;




// "admin@ship.ru"   "secret2"

const LoginPage = ( {
                      input_username, 
                      input_password, 
                      input_rest, 
                      add_contracts,
                      token   } 
                  ) => {

    console.log('\n\n Is there token accessable?', token ,'\n\n')


    return <div className='login-page'>
        <InputLogin name="iwtd"/>

        <form>
          <input 
            placeholder="Логин"
            className='login-form'
            onChange={ (event) => {
              event.preventDefault()
              input_username(event.target.value) } 
            }>

          </input>

          <input
            placeholder="Пароль"
            className='password-form'
            onChange={ (event) => {
              event.preventDefault()
              input_password(event.target.value) } 
            }>

          </input>

          <button 
            className='submit-button'
            onClick={ async function (event){   // async - only way to use await 
                        event.preventDefault()

                        /*  Login code:

                          // **** temporary measure ****
                          // TODO: see docs to know: how to get this poles to different users 
                          input_rest({ 
                             client_id: '1',
                             client_secret: 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT',
                             grant_type: 'password'})

                          // to call thunk(nested function) - we need ()() 
                          const response = await Login()()
                          console.log('got response on login:', response)
                          update_token( getApiKey(response) )
                          add_contracts( await getContracts(token) )
                        */
                      } 
                    }>
              Войти
          </button>

        </form>

    </div>

}



const mapStateToProps = (state) => {
  const {sessionData} = state
  const {token} = sessionData
  return token
}


// wrapping actions into dispatch
const mapDispatchToProps = {
  input_username, 
  input_password, 
  input_rest,
  add_contracts }



export default connect(mapStateToProps, mapDispatchToProps)(LoginPage);
