import React from 'react';
import { connect } from 'react-redux';
import { 
         input_username, 
         input_password, 
         input_rest,
         update_token, 
         add_contracts,
         Login
         }    from '../../actions';

import {getApiKey, getContracts} from '../../services';

import './login-page.css';

import styled from 'styled-components'



const InputLogin = styled.input`
  position: relative;
  width: 342px;
  height: 50px;
  left: 549px;

  top: 443px;

  background: rgba(0, 0, 0, 0.1);
  border-radius: 10px;
`


// password: top: 513px;


const SubmitButton = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 20px;

  position: absolute;
  width: 342px;
  height: 50px;
  left: 549px;
  top: 583px;

  background: #0F75BF;
  border-radius: 4px;
`






// "admin@ship.ru"   "secret2"

const LoginPage = ( {
                      input_username, 
                      input_password, 
                      input_rest, 
                      add_contracts,
                      token   } 
                  ) => {

    console.log('\n\n Is there token accessable?', token ,'\n\n')


    return <div className='login-page'>



        <form>

          <InputLogin 
            placeholder='Логин'
            onChange={ (event) => {
              event.preventDefault()
              input_username(event.target.value)}}/>

          <InputLogin 
            placeholder='Пароль'
            onChange={ (event) => {
              event.preventDefault()
              input_password(event.target.value)}}/>


          <SubmitButton
            placeholder='Войти'
            onClick={ async function (event){   // async - only way to use await 
                        event.preventDefault()
                        // **** temporary measure ****
                        // TODO: see docs to know: how to get this poles to different users 
                        input_rest({ 
                           client_id: '1',
                           client_secret: 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT',
                           grant_type: 'password'})
                        /*  Login code:
                          // to call thunk(nested function) - we need ()() 
                          const response = await Login()()
                          console.log('got response on login:', response)
                          update_token( getApiKey(response) )
                          add_contracts( await getContracts(token) )
                        */
            }}/>



        </form>

    </div>

}



const mapStateToProps = (state) => {
  const {sessionData} = state
  const {token} = sessionData
  return token
}


// wrapping actions into dispatch
const mapDispatchToProps = {
  input_username, 
  input_password, 
  input_rest,
  add_contracts }



export default connect(mapStateToProps, mapDispatchToProps)(LoginPage);
