import React, {useState} from 'react';
import './login-page.css';


const onLoginClick = (event) => {

  event.preventDefault();               // prevent default actions(e.g. page refreshing => loose data)
  let formdata = new FormData();
  formdata.append("username", "admin@ship.ru");
  formdata.append("password", "secret2");
  formdata.append("client_id", "1");
  formdata.append("client_secret", "c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT");
  formdata.append("grant_type", "password");

  let requestOptions = {
    method: 'POST',
    body: formdata,
    redirect: 'follow'
  };

  fetch("http://server.clerc.ru/api/v2.0/auth/login", requestOptions)
    .then(response => response.text())
    .then(result => console.log('on login response',result))
    .catch(error => console.log('error', error));

}


const LoginPage = () => {
    [username, setUsername] = useState('');
    [password, setPassword] = useState('');

    return <div className='login-page'>

        <form>
          <input className='login-form'>
          </input>

          <input className='password-form'>
          </input>

          <button 
            className='submit-button'
            onClick={ onLoginClick }>
              Войти
          </button>

        </form>

    </div>

}

export default LoginPage;
