const updateContracts = (state, action) =>{

    if (state === undefined || state.contractsPageData === undefined)
        return {   contracts: [], todo:''   }

    const contractsPageData = { ...state.contractsPageData }

    switch(action.type){
        case 'ADD_CONTRACTS':
            return {...contractsPageData,  contracts: action.payload}

    }


}

export default updateContracts;
