const updateGlobalparams = (state, action) =>{

    if (state === undefined )
        return {  apiToken: '' }

    switch(action.type){
        case 'UPDATE_TOKEN':
            return {...state,  apiToken: action.payload}

    }


}

export default updateGlobalparams;
