const updateContracts = (state, action) =>{

    if (state === undefined )
        return { contractsPageData: { contracts: [] }  }

    switch(action.type){
        case 'ADD_CONTRACTS':
            return {...state,  contracts: action.payload}

    }


}

export default updateContracts;
