const updateLoginData = (state, action) => {

    if (state === undefined )
        return{
                    username: '',
                    password: '',
                    client_id: '', 
                    client_secret: '',
                    grant_type: ''     
        }

    const payload = action.payload
    const loginFormData = { ...state.loginFormData }


    switch (action.type) {

      case 'INPUT_USER':
        return { ...loginFormData, username: payload }

      case 'INPUT_PSWD':
        return { ...loginFormData, password: payload }

      case 'INPUT_REST':
        const {
          client_id, 
          client_secret, 
          grant_type    } = payload

        return { 
                  ...loginFormData,
                  client_id, 
                  client_secret, 
                  grant_type
                }  

      default:
        return state
    }

  };

export default updateLoginData
