import React from 'react';
import { connect } from 'react-redux';
import { 
         input_username, 
         input_password, 
         input_rest,
         update_token, 
         add_contracts
         }    from '../../actions';

import {Login, getApiKey, getContracts} from '../../services';

import './login-page.css';


// "admin@ship.ru"   "secret2"

const LoginPage = ( {
                      input_username, 
                      input_password, 
                      input_rest, 
                      loginFormData,
                      sessionData   } 
                  ) => {

    const { token } = sessionData



    return <div className='login-page'>

        <form>
          <input 
            className='login-form'
            onChange={ (event) => {
              event.preventDefault()
              input_username(event.target.value) } 
            }>

          </input>

          <input 
            className='password-form'
            onChange={ (event) => {
              event.preventDefault()
              input_password(event.target.value) } 
            }>

          </input>

          <button 
            className='submit-button'
            onClick={ async function (event){
                        event.preventDefault()

                        // **** temporary measure ****
                        // TODO: see docs to know: how to get this poles to different users 
                        input_rest({ 
                           client_id: '1',
                           client_secret: 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT',
                           grant_type: 'password'})

                        const response = await Login()
                        update_token( getApiKey(response) )
                        add_contracts( await getContracts(token) )
                      } 
                    }>
              Войти
          </button>

        </form>

    </div>

}



const mapStateToProps = (state) => {
  const {sessionData} = state
  return sessionData
}


// wrapping actions into dispatch
const mapDispatchToProps = {
  input_username, 
  input_password, 
  input_rest }



export default connect(mapStateToProps, mapDispatchToProps)(LoginPage);
