const updateLoginData = (state, action) => {
    console.log(`action type  ${action.type} and payload ${action.payload}`);

    if (state === undefined )
        return{
                loginFormData:{
                    username: '',
                    password: '',
                    client_id: '', 
                    client_secret: '',
                    grant_type: ''     }
        }


    const payload = action.payload
    console.log('\nredux-store: ', state)

    const loginFormData = { ...state.loginFormData }


    switch (action.type) {

      case 'INPUT_USER':
        console.log('Username: ', payload)
        return { ...loginFormData, username: payload }

      case 'INPUT_PSWD':
        return { ...loginFormData, password: payload }

      case 'INPUT_REST':

        const {
          client_id, 
          client_secret, 
          grant_type    } = payload

        return { 
                  ...loginFormData,
                  client_id, 
                  client_secret, 
                  grant_type
                }  

      /*
      // TODO: 1) remove to ../services - bc LOGIN makes no change on storage - just "side effect"
      //       2) mb not to create FormData each api-call 
      case 'LOGIN':
        let formdata = new FormData()
        formdata.append("username", username)
        formdata.append("password", password)
        formdata.append("client_id", client_id)
        formdata.append("client_secret", client_secret)
        formdata.append("grant_type", grant_type)

        let requestOptions = {
          method: 'POST',
          body: formdata,
          redirect: 'follow'
        }

        return fetch("http://server.clerc.ru/api/v2.0/auth/login", requestOptions)
               .then(response => response.text())
               .then(result => console.log('on login response',result))
               .catch(error => console.log('error', error))

    */

      default:
        return state
    }

  };

export default updateLoginData
