import React from 'react';
import { connect } from 'react-redux';
import { 
         input_username, 
         input_password, 
         input_rest,
         add_contracts,
         update_token
         }    from '../../actions';

import Login from '../../services';

import './login-page.css';


// "admin@ship.ru"   "secret2"

const LoginPage = ({
                    input_username, 
                    input_password, 
                    input_rest }) => {

    return <div className='login-page'>

        <form>
          <input 
            className='login-form'
            onChange={ (event) => {
              event.preventDefault()
              //console.log('Input is:', event.target.value)
              input_username(event.target.value) } 
            }>

          </input>

          <input 
            className='password-form'
            onChange={ (event) => {
              event.preventDefault()
              input_password(event.target.value) } 
            }>

          </input>

          <button 
            className='submit-button'
            onClick={ async function (event){
                        event.preventDefault()

                        // **** temporary measure ****
                        // TODO: see docs to know: how to get this poles to different users 
                        input_rest({ 
                           client_id: '1',
                           client_secret: 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT',
                           grant_type: 'password'})

                        const response = await login()
                        const apiKey = getApiKey(response)
                        console.log('\njwt aka api-key:\n', apiKey)

                        const contracts = await getContracts(apiKey)
                        console.log('\n\ncontracts :\n', contracts, '\n-------------------\n')

                      } 
                    }>
              Войти
          </button>

        </form>

    </div>

}



const mapStateToProps = (state) => {
  const {loginFormData} = state
  return loginFormData
}


// wrapping actions into dispatch
const mapDispatchToProps = {
  input_username, 
  input_password, 
  input_rest }



export default connect(mapStateToProps, mapDispatchToProps)(LoginPage);
