import updateLoginData    from "./login-form"
import updateContracts    from "./contracts-page"
import updateSession      from "./session-data"      


const reducer = (state, action = { payload: {} }) => {
    return{
        sessionData:        updateSession(state, action),
        loginFormData:      updateLoginData(state, action),
        contractsPageData:  updateContracts(state, action)

    }
}

export default reducer
