// each cart is button itself and has 2 buttons inside: coppy and properties !!!
// CSS - from figma


import React from 'react'
import styled from 'styled-components'
import './contract-card.css'



const Item = styled.div`
  font-family: 'Gotham Pro';
  font-style: normal;
  font-weight: 400;
  font-size: 14px;
  line-height: 125%;

  color: #333333;   
`

const Card = styled.li`
    box-sizing: border-box;

    position: absolute;
    width: 1900px;
    height: 200px;
    left: 10px;
    top: 322px;

    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 10px;
`


const ContractCard = (props) => {

    const { info, onCardSelected} = props
    const {id} = info


    const items = Object.values(info).map((item) => {
                  return (
                    <Item>
                       {item}
                    </Item>
                  )
    })

    return (
      <Card id={id} onClick={(id) => onCardSelected(id)}>
        {items}
      </Card>
    )
  }






export default Card;
