import React from 'react';
//  import {Routes, Route} from 'react-router-dom';
import LoginPage from '../pages';   
import ContractCard from '../contractCard';

const login = 'admin@ship.ru';
const pass = 'secret2';
const secret = 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT';

const dummyContract = {id: 88, name: 'Is it even rendered?', description: 'Another big ship'}

/*

    const  url = 'server.clerc.ru/api/v2.0/auth/login'

    Проблема с тем, что не получается сделать запрос на другой домен
    (бразуер приписывает локал хост в начало урла)

    А тело запроса - уже правильное

    let loginForm = new FormData();
    loginForm.append('username', login);
    loginForm.append('password', pass);
    loginForm.append('client_id', 1);
    loginForm.append('grant_type', 'password');
    loginForm.append('client_secret', secret);

    fetch(url, {
        method: 'POST',
        headers: {
            crossDomain: true},

        body: loginForm
    }).then(res => 
        console.log('RESPONSE???:', res));
*/


let formdata = new FormData();
formdata.append("username", "admin@ship.ru");
formdata.append("password", "secret2");
formdata.append("client_id", "1");
formdata.append("client_secret", "c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT");
formdata.append("grant_type", "password");

let requestOptions = {
  method: 'POST',
  body: formdata,
  redirect: 'follow'
};

fetch("http://server.clerc.ru/api/v2.0/auth/login", requestOptions)
  .then(response => response.text())
  .then(result => console.log('On login response:',result))
  .catch(error => console.log('error', error));



const App = () => {
    return <LoginPage/>;    
}

export default App;
