const updateSession = (state, action) =>{

    if (state === undefined )
        return {  sessionData: { apiToken: '' } }

    const sessionData = {... state.sessionData}

    switch(action.type){
        case 'UPDATE_TOKEN':
            return {...state,  apiToken: action.payload}

    }


}

export default updateSession
