// each cart is button itself and has 2 buttons inside: coppy and properties !!!
// CSS - from figma


import React from 'react'
import styled from 'styled-components'
import './contract-card.css'



const Item = styled.div`
  font-family: 'Gotham Pro';
  font-style: normal;
  font-weight: 400;
  font-size: 14px;
  line-height: 125%;

  color: #333333;   
`



const ContractCard = (props) => {

    const { info, onCardSelected} = props

    const {
      id,
      position,
      name,
      counterpart,
      number,
      date,
      price,
      deadline,
      note,
      program_name} = info

    const contracts = [{anot: 'Наименование', name},    
                       {anot:'Номер гос. контракта', number},
                       {anot: 'Дата подписания контракта', date},
                       {anot:'Окончание выполнения работ', deadline},
                       {anot:'Цена контракта',  price},
                       {anot:'Наименование программы', program_name},
                       {anot:'Контрагент', counterpart},
                       {anot:'Примечание', note}
                      ]



    const items = Object.values(contracts).map((contract) => {
                  const {anot, item} = Object.values(contract) 
                  return (
                    <Item>
                        {anot}: {item ? item : ''}
                    </Item>  )
    })

    return (
      <li className='card' key={id} onClick={(id) => onCardSelected(id)}>
        {items}
      </li>
    )
  }



const StyledCard = styled(ContractCard)`
  box-sizing: border-box;

  position: absolute;
  width: 1900px;
  height: 200px;
  left: 10px;
  top: 322px;

  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: 10px;
`


export default StyledCard
