import React from 'react';
import LoginPage from '../pages';


const login = 'admin@ship.ru';
const pass = 'secret2';
const secret = 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT';

const  url = 'server.clerc.ru/api/v2.0/auth/login'

fetch(url, {
    method: 'POST',
    headers: {
        'scheme': 'http',
        'host': 'server.clerc.ru',
        'filename': '/api/v2.0/auth/login'
    },

    body: JSON.stringify({
        'username': login,
        'password': pass,
        'client_id': 1,
        'client_secret': secret,
        'grant_type': password
    })
}).then(response => response.json()).then(data => {
    console.log(data);
    return data;
});



const App = () => {
    return <LoginPage/>;
}

export default App;
