const updateSession = (state, action) =>{

    if (state === undefined || state.sessionData === undefined )
        return {   apiToken: ''  }

    const sessionData = {...state.sessionData}



    switch(action.type){
        case 'UPDATE_TOKEN':
            return { ...sessionData,  apiToken: action.payload}
        default:
            return sessionData
    }


}

export default updateSession
