// To call reducer`s 'api' we need to call  store.dispatch( {type: ACTION_NAME} )
// so here we served action {type: ACTION_NAME} for each api`s function


export const input_username = (username) => { return {type: 'INPUT_USER', payload: username}} 
export const input_password = (password) => { return {type: 'INPUT_PSWD', payload: password} }
export const input_rest     = (rest)     => { return {type: 'INPUT_REST', payload: rest} }
export const login          = ()         => { return {type: 'LOGIN' } }
