import React from 'react';
import { connect } from 'react-redux';
import { 
         input_username, 
         input_password, 
         input_rest,
         update_token, 
         add_contracts,
         Login
         }    from '../../actions';

import {getApiKey, getContracts} from '../../services';


import styled from 'styled-components'



const InputLogin = styled.input`
  position: absolute;
  width: 342px;
  height: 50px;
  left: 549px;

  top: ${(props) => props.top};

  background: rgba(0, 0, 0, 0.1);
  border-radius: 10px;
`




// password: top: 513px;


const SubmitButton = styled.button`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 20px;

  position: absolute;
  width: 342px;
  height: 50px;
  left: 549px;
  top: 583px;

  background: #0F75BF;
  border-radius: 4px;
`


const Vave = styled.div`
position: absolute;
width: 152px;
height: 5px;
left: 644px;
top: 383px;

border: 8px solid #0DA1E0;
`



const Background = styled.div`
  position: absolute;
  width: 1440px;
  height: 1024px;
  left: 0px;
  top: 0px;
  background:linear-gradient(116.82deg, #364F6B 0%, #47688D 100%);
`





// "admin@ship.ru"   "secret2"


const LoginPage = ( {
                      input_username, 
                      input_password, 
                      input_rest, 
                      add_contracts,
                      token   } 
                  ) => {

    console.log('\n\n Is there token accessable?', token ,'\n\n')


    // classname 
    return <Background>

          <Vave/>

        <form>          
          <InputLogin 
            placeholder='Логин'
            top='443px'
            onChange={ (event) => {
              event.preventDefault()
              input_username(event.target.value)}}/>

          <InputLogin 
            placeholder='Пароль'
            top='513px'
            onChange={ (event) => {
              event.preventDefault()
              input_password(event.target.value)}}/>

          <SubmitButton
            onClick={ async function (event){   // async - only way to use await 
                        event.preventDefault()
                        // **** temporary measure ****
                        // TODO: see docs to know: how to get this poles to different users 
                        input_rest({ 
                           client_id: '1',
                           client_secret: 'c75IGwuqkjrO1RWCE4Ntn4zqpQdpgnEO2wGT9iMT',
                           grant_type: 'password'})

                        /*  Login code:
                          // to call thunk(nested function) - we need ()() 
                          const response = await Login()()
                          console.log('got response on login:', response)
                          update_token( getApiKey(response) )
                          add_contracts( await getContracts(token) )
                        */
            }}>
              Войти
          </SubmitButton>



        </form>


    </Background>

}



const mapStateToProps = (state) => {
  const {sessionData} = state
  const {token} = sessionData
  return token
}


// wrapping actions into dispatch
const mapDispatchToProps = {
  input_username, 
  input_password, 
  input_rest,
  add_contracts }



export default connect(mapStateToProps, mapDispatchToProps)(LoginPage);
